    public function getSourceContext()
    {
        return new Source("{% extends 'layout.html.twig' %}

{% block title %}Please login{% endblock %}

{% block stylesheets %}
    <link href=\"https://cdn.jsdelivr.net/npm/bootstrap@5.2.3/dist/css/bootstrap.min.css\" rel=\"stylesheet\" integrity=\"sha384-rbsA2VBKQhggwzxH7pPCaAqO46MgnOM80zW1RWuH61DGLwZJEdK2Kadq2F9CUG65\" crossorigin=\"anonymous\">
    <link rel=\"stylesheet\" href=\"{{ asset('css/main.css') }}\">
    <link rel=\"stylesheet\" href=\"{{ asset('css/form.css') }}\">
{% endblock %}

{% block signIn %}
    <button class=\"btn btn-light\" onclick=\"window.location = '{{path('app_register') }}'\">Register</button>
{% endblock %}

{% block sidebar %}
    <div class=\"row\">
        <div class=\"col-12 d-flex justify-content-center form-img\">
            <img src=\"{{ asset('img/payment-1.png') }}\" alt=\"payment\" class=\"img\">
        </div>
    </div>
{% endblock %}

{% block main_content %}
    {% if error %}
        <div>{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}
    <div class=\"row\">
        <div class=\"col-12 d-flex justify-content-center\">
            <h1>Login</h1>
        </div>
    </div>
    <div class=\"form\">
        <form action=\"{{ path('app_login') }}\" method=\"post\">

            <div class=\"row\">
                <div class=\"col-12\">
                    <label for=\"username\">Email:</label>
                    <input type=\"text\" id=\"username\" class=\"input-field\" name=\"_username\" value=\"{{ last_username }}\">
                </div>
            </div>


            <label for=\"password\">Password:</label>
            <input type=\"password\" id=\"password\" class=\"input-field\" name=\"_password\">

            <input type=\"hidden\" name=\"_target_path\" value=\"/profile\">

            <div class=\"row\">
                <div class=\"col col-xl-3\">
                    <button type=\"submit\" class=\"btn btn-success\">Login</button>
                </div>
                <div class=\"col col-xl-9\">
                    <p>If you have no account, you need to <a href=\"{{ path('app_register') }}\">register</a>!</p>
                </div>
            </div>
        </form>
    </div>

{% endblock %}
", "login/index.html.twig", "/app/templates/login/index.html.twig");
    }
